import React, { useState, useEffect } from "react";
import "./App.css";

const sentences = [
  "Clear your mind",
  "You did well",
  "Think of something peaceful",
  // Additional sentences...
];

function App() {
  const [currentSentenceIndex, setCurrentSentenceIndex] = useState(0);
  const [phase, setPhase] = useState("fade-in"); // 'fade-in', 'visible', 'fade-out'

  useEffect(() => {
    // Sequence: Fade In -> Visible -> Fade Out
    let timeoutId;

    if (phase === "fade-in") {
      timeoutId = setTimeout(() => setPhase("visible"), 2000); // Stay visible after fading in
    } else if (phase === "visible") {
      timeoutId = setTimeout(() => setPhase("fade-out"), 2000); // Fade out after being visible
    } else if (phase === "fade-out") {
      timeoutId = setTimeout(() => {
        setCurrentSentenceIndex(
          (prevIndex) => (prevIndex + 1) % sentences.length
        );
        setPhase("fade-in");
      }, 2000); // Move to next sentence and fade in
    }

    return () => clearTimeout(timeoutId);
  }, [phase, currentSentenceIndex]);

  return (
    <div className="App">
      <div className={`sentence ${phase}`}>
        {sentences[currentSentenceIndex]}
      </div>
    </div>
  );
}

export default App;
